package com.cg.SampleFor.ui;

public class MainFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
            String veenschar[]= {"V","E","E","N","A"};
            for(String str:veenschar)
            {
            	System.out.println(str);
            }
            for(int i=0;i<=veenschar.length-1;i++)
            {
            	System.out.println(veenschar[i]);
            }
            int i=0;
            while(i<15)
            {
            	System.out.println(" "+i);
                i++;
                
            }
	}

}
